mapper.update:
UPDATE t_owner
SET pw = #{reset_pw}
WHERE pk = #{owner_pk}

UPDATE t_rstrnt
enabled = true, contact = #{contact}, location = #{location}, more_info = #{more_info}, profile_img = #{profile_img},
WHERE pk = #{pk}

UPDATE t_rstrnt
SET state = #{state}
WHERE pk = #{pk}

UPDATE t_reserv
SET process_status = #{process_status}, last_update = now()
WHERE pk = #{pk}

mapper.select:
SELECT pk, nm, location, contact, profile_img FROM t_rstrnt WHERE (state = 0 OR state = 1) AND enabled = true
AND city_pk = #{city_pk}
AND nm LIKE CONCAT('%', #{nm}, '%')
